import Layout from '../components/layout'
import {Radio} from 'antd'
import {checkProjects,historys} from '../utils/config'
import moment from "moment"
export default class extends React.Component {
  static async getInitialProps({ req,query,jsonPageRes }) {
    const userAgent = req ? req.headers['user-agent'] : navigator.userAgent
    return { userAgent,query,jsonPageRes }
  }
  state = {
    name:"",
    sex:1,
    age:1,
    phone:"",
    appointment_date:"",
    time_bucket:1,
    check_project:1,
    check_project_lable:"",
    check_method:1,
    history:1,
    allergy_history:"",
    allergy_history_lable:"",
    remark:""
  }

  async loadList(tp){

  }

  async componentDidMount(){

  }
  choiceDate(){
    let self = this
    weui.datePicker({
      start: new Date(moment().add(1, 'days')), // 从今天开始
      end: 2030,
      // defaultValue: [2020, 6, 9],
      onChange: function(result){
          //console.log(result);
      },
      onConfirm: function(result){
          //console.log(11122,result);
          if (result[1].value < 10){
            result[1].value = `0${result[1].value}`
          }
          if (result[2].value < 10){
            result[2].value = `0${result[2].value}`
          }
          self.setState({appointment_date:result[0].value+"-"+result[1].value+"-"+result[2].value})
      },
      id: 'datePicker'
    });
  }

  checkProJect(){
    let self = this
    weui.picker(checkProjects, {
         className: 'custom-classname',
         container: 'body',
         defaultValue: [1],
         onChange: function (result) {

         },
         onConfirm: function (result) {
             self.setState({check_project_lable:result[0].label,check_project:result[0].value})
         },
         id: 'checkProJect'
      });
  }

  choiceAllergyHistory(){
    let self = this
    weui.picker(historys, {
         className: 'custom-classname',
         container: 'body',
         defaultValue: [1],
         onChange: function (result) {

         },
         onConfirm: function (result) {
             self.setState({allergy_history_lable:result[0].label,allergy_history:result[0].value})
         },
         id: 'historys'
      });
  }

  subMit(){
    console.log(this.state)
  }

  render() {
